  const [desc, setDesc] = useState("");
  const [amount, setAMount] = useState("");
  const [isExpense, setExpense] = useState("");

  const handleSave = () => {
    if (!desc || !amount) {
      alert("Informe a descrição e o valor");
      return;
    } else if (amount < 1) {
      alert("O valor tem que ser positivo!");
      return;
    }
  };

  return <div>Form</div>;
};

export default Form;
